(function( window, undefined ) {
    ht.customer={
        init:function(){
            this.bindEvent();
        },
        bindEvent:function(){
            $('.customerLeftNav h3.tit').click(function(){
                var $obj=$(this).parent();
                if($obj.hasClass('expanded')){
                    $obj.removeClass('expanded').addClass('collapsed');
                }else{
                    $obj.removeClass('collapsed').addClass('expanded');
                }

            });
        }
    }
    $(function(){
       ht.customer.init();
    });
})(window);
